 <html>      <head>             <title>Live Table Data Edit</title>  	</head>        <body>             <div id="live_data" style="width:1040px; height:450px; margin-left:48px; margin-top:10px;"></div>      </body> 		    <script src="./js/sorttable.js"></script> 			<script src="./js/jquery.min.js"></script>   		   	<script src="./js/bootstrap.min.js"></script>      <script>   $(document).ready(function(){        function fetch_data()        {             $.ajax({                  url:"./mod/select.php",                  method:"POST",                 success:function(data){                       $('#live_data').html(data);                  }             });        }        fetch_data();        $(document).on('click','#btn_add', function(){  			var idsolicitud = $('#idsolicitud').text();			var fecha = $('#fecha').text();			var titulo = $('#titulo').text();			var link = $('#link').text();			var comment = $('#comment').text();			var seccionid = $('#seccionid').text();			var clave1 = $('#clave1').text();			             if(idsolicitud =='')             {                  alert("Enter idsolicitud");                  return false;             }             if(fecha =='')             {                  alert("Enter fecha");                  return false;             }           if(titulo =='')             {                  alert("Enter titulo");                  return false;             }           if(link =='')             {                  alert("Enter link");                  return false;             }           if(comment =='')             {                  alert("Enter comment");                  return false;             }           if(seccionid =='')             {                  alert("Enter seccionid");                  return false;             }           if(clave1 =='')             {                  alert("Enter clave1");                  return false;             }		              $.ajax({                  url:"./mod/insert.php",                  method:"POST",                  data:{idsolicitud:idsolicitud,fecha:fecha,titulo:titulo,link:link,comment:comment,seccionid:seccionid,clave1:clave1},                  dataType:"text",                  success:function(data)                  {                       alert(data);                       fetch_data();                  }             })        });        function edit_data(id, text, column_name)        {  			           $.ajax({                  url:"./mod/edit.php",                  method:"POST",                data:{id:id, text:text, column_name:column_name},                  dataType:"text",                  success:function(data){                       alert(data);                  }             });        }		$(document).on('blur','.idsolicitud', function(){			var id = $(this).data("id1");			var idsolicitud = $(this).text();			edit_data(id, idsolicitud, "idsolicitud");		});		 		$(document).on('blur','.fecha', function(){			 var id = $(this).data("id2");			 var fecha = $(this).text();			 edit_data(id, fecha, "fecha");		 });		$(document).on('blur','.titulo', function(){			 var id = $(this).data("id3");			 var titulo = $(this).text();		 edit_data(id, titulo, "titulo");		 });		 $(document).on('blur','.link', function(){			 var id = $(this).data("id3");			 var link = $(this).text();		 edit_data(id, link, "link");		 });		  $(document).on('blur','.comment', function(){			 var id = $(this).data("id3");			 var comment = $(this).text();		 edit_data(id, comment, "comment");		 });		 $(document).on('blur','.seccionid', function(){			 var id = $(this).data("id3");			 var seccionid = $(this).text();		 edit_data(id, seccionid, "seccionid");		 });		  $(document).on('blur','.clave1', function(){			 var id = $(this).data("id3");			 var clave1 = $(this).text();		 edit_data(id, clave1, "clave1");		 });		 		 		 		 		        $(document).on('click','.btn_delete', function(){             var id = $(this).data("id4");             if(confirm("Are you sure you want to delete this?"))             {                  $.ajax({                       url:"./mod/delete.php",                       method:"POST",                       data:{id:id},                       dataType:"text",                       success:function(data){                            alert(data);                            fetch_data();                       }                  });             }        });   });   </script> </html>