 <html>      <head>             <title>Live Table Data Edit</title>  	</head>        <body>             <div id="live_data" style="width:1040px; height:450px; margin-left:48px; margin-top:10px;"></div>      </body> 		    <script src="./js/sorttable.js"></script> 			<script src="./js/jquery.min.js"></script>   		   	<script src="./js/bootstrap.min.js"></script>      <script>   $(document).ready(function(){        function fetch_data()        {             $.ajax({                  url:"./mod/select.php",                  method:"POST",                 success:function(data){                       $('#live_data').html(data);                  }             });        }        fetch_data();        $(document).on('click','#btn_add', function(){  			var servicio = $('#servicio').text();			var activo = $('#activo').text();			var tiposervicio = $('#tiposervicio').text();			             if(servicio =='')             {                  alert("Enter servicio");                  return false;             }             if(activo =='')             {                  alert("Enter activo");                  return false;             }           if(tiposervicio =='')             {                  alert("Enter tiposervicio");                  return false;             }		              $.ajax({                  url:"./mod/insert.php",                  method:"POST",                  data:{servicio:servicio, activo:activo, tiposervicio:tiposervicio},                  dataType:"text",                  success:function(data)                  {                       alert(data);                       fetch_data();                  }             })        });        function edit_data(id, text, column_name)        {             $.ajax({                  url:"./mod/edit.php",                  method:"POST",                data:{id:id, text:text, column_name:column_name},                  dataType:"text",                  success:function(data){                       alert(data);                  }             });        }		$(document).on('blur','.servicio', function(){			var id = $(this).data("id1");			var servicio = $(this).text();			edit_data(id, servicio, "servicio");		});		 		$(document).on('blur','.activo', function(){			 var id = $(this).data("id2");			 var activo = $(this).text();			 edit_data(id, activo, "activo");		 });		$(document).on('blur','.tiposervicio', function(){			 var id = $(this).data("id3");			 var tiposervicio = $(this).text();		 edit_data(id, tiposervicio, "tiposervicio");		 });			 		        $(document).on('click','.btn_delete', function(){             var id = $(this).data("id4");             if(confirm("Are you sure you want to delete this?"))             {                  $.ajax({                       url:"./mod/delete.php",                       method:"POST",                       data:{id:id},                       dataType:"text",                       success:function(data){                            alert(data);                            fetch_data();                       }                  });             }        });   });   </script> </html>