CREATE TABLE `enlaces` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `idsolicitud` int(11) DEFAULT NULL,
  `fecha` date NOT NULL DEFAULT '0000-00-00',
  `titulo` varchar(255) COLLATE utf8_spanish_ci NOT NULL DEFAULT '',
  `link` varchar(255) COLLATE utf8_spanish_ci NOT NULL DEFAULT '',
  `comment` text CHARACTER SET latin1 COLLATE latin1_spanish_ci,
  `seccionid` int(11) NOT NULL DEFAULT '0',
  `clave1` varchar(128) CHARACTER SET utf8 DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=72 DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

