  `id` int(11) NOT NULL AUTO_INCREMENT,
  `fecha` date NOT NULL,
  `analisisobjetivos` text COLLATE utf8_spanish_ci NOT NULL,
  `analisissatisfaccionclientes` text COLLATE utf8_spanish_ci NOT NULL,
  `analisisreclamacionesclientes` text COLLATE utf8_spanish_ci NOT NULL,
